const json = $input.item.json || {};
const bin = $input.item.binary || {};

function safe(v) { return String(v ?? '').trim(); }
function applyVars(template, vars) {
  let out = String(template || '');
  for (const [k, v] of Object.entries(vars)) {
    out = out.split(`{{${k}}}`).join(String(v ?? ''));
  }
  return out;
}

const pdfUrl = safe(json.pdf_url);
const origin = pdfUrl ? (new URL(pdfUrl)).origin.replace(/\/+$/,'') : '';
const quotation = json.quotation || {};
const customer = json.customer || {};
const quotationId = safe(quotation.id);
const quotationNumber = safe(quotation.quotation_number) || quotationId;

// file id returned by Directus /files
const fileId = safe(json?.data?.id || json?.id);
const pdfLink = fileId && origin ? `${origin}/assets/${fileId}?download=1` : '';

const toEmail = safe(quotation.sent_to_email || customer.email);

const vars = {
  company_name: safe(customer.company_name),
  contact_name: safe(customer.contact_name),
  quotation_number: quotationNumber,
  pdf_link: pdfLink,
};

const subjectTpl = safe(json.email_template_subject) || 'Orçamento {{quotation_number}} — HOTELEQUIP.PT';
const htmlTpl = safe(json.email_template_html) || `<!doctype html><html><body style="font-family:Arial,sans-serif;line-height:1.5;color:#111"><p>Olá {{company_name}},</p><p>Segue em anexo o orçamento <strong>{{quotation_number}}</strong>.</p><p>Link do PDF (caso necessário): <a href="{{pdf_link}}">{{pdf_link}}</a></p><p>Obrigado,<br/>HOTELEQUIP.PT</p></body></html>`;

// Download fichas técnicas (best-effort) -> tech_* binaries
const list = Array.isArray(json.technical_pdfs) ? json.technical_pdfs : [];
for (let i = 0; i < list.length; i++) {
  const t = list[i] || {};
  const url = safe(t.url);
  const sku = safe(t.sku);
  if (!url) continue;

  try {
    const resp = await this.helpers.httpRequest({
      method: 'GET',
      url,
      encoding: null,
      followRedirect: true,
      timeout: 120000,
      headers: {
        'User-Agent': 'CRM-HOTELEQUIP (n8n)',
        Accept: 'application/pdf,application/octet-stream;q=0.9,*/*;q=0.8',
      },
    });
    const data = Buffer.isBuffer(resp) ? resp : Buffer.from(resp);
    const key = `tech_${(sku || String(i + 1)).replace(/[^a-zA-Z0-9_-]/g, '_').slice(0, 40)}`;
    const fileName = `${sku || `ficha-tecnica-${i + 1}`}.pdf`;
    bin[key] = await this.helpers.prepareBinaryData(data, fileName);
  } catch {
    // ignore
  }
}

return [
  {
    json: {
      ...json,
      quotationId,
      fileId,
      pdfLink,
      email_to: toEmail,
      email_subject: applyVars(subjectTpl, vars),
      email_html: applyVars(htmlTpl, vars),
      directus_origin: origin,
    },
    binary: bin,
  },
];

const draftId = $node['Outlook - Create a draft'].json.id;
const base = $input.item.json;
const bin = $input.item.binary || {};

return Object.keys(bin).map((key) => ({
  json: {
    ...base,
    draftId,
    attachment_key: key,
    attachment_filename: bin[key]?.fileName || `${key}.pdf`,
  },
  binary: {
    data: bin[key],
  },
}));
